import React, { useEffect, useState } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { selectUserInput, setBlogData } from "../reducers";
import BlogComments from './BlogComments'
import "../styling/blogs.css";
<link rel="stylesheet" href="path/to/font-awesome/css/font-awesome.min.css"></link>



const Blogs = () => {
  const [selectedBlogId, setSelectedBlogId] = useState(null);

  const handleViewCommnets = (event) => {
    console.log("handleViewCommnets method called");
    console.log("event ::",event);
    console.log("event.target.id :",event.target.id);
    // selectedBlogId = event.target.id;
    setSelectedBlogId(event.target.id);
  } 

  const searchInput = useSelector(selectUserInput);
  console.log("Going to fetch API");
  // const blog_url =fetch('https://jsonplaceholder.typicode.com/todos/1')
  // .then(response => response.json())
  // .then(json => {
  // })
  console.log("After API call");
  const dispatch = useDispatch();
  const [lstBlogs, setBlogs] = useState();

  const [loading, setLoading] = useState(true);

  useEffect(() => { 
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((response) => {
        console.log("Data:", response.data);
        dispatch(setBlogData(response.data));
        setBlogs(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [searchInput]);
  console.log("Test");

  return (
    <div className="blog__page">
      <h1 className="blog__page__header">Blogs</h1>
      {loading ? <h1 className="loading">Loading...</h1> : ""}
      <div className="blogs">
        {lstBlogs?.map((blogObj) => (
          
          <a
            className="blog"
            target="_blank"
            href={blogObj?.url}
            rel="noopener noreferrer"
            key={blogObj.id}
          >
            <div>
              <h3 className="sourceName">
                <span>{blogObj?.title}</span> 
                <p>{blogObj?.publishedAt}</p>
              </h3>
              <p>{blogObj?.body}</p>
              {/* <div className="commentCls" id={blogObj?.id} onClick={handleViewCommnets} >ðŸ’¬</div> */}
              <button className="commentCls" id={blogObj?.id} onClick="">ðŸ’¬</button>
              <i class="fa-solid fa-comment"></i>
              <div> {selectedBlogId == blogObj?.id && (<BlogComments data={selectedBlogId}/>) }  </div>
            </div>
          </a>
        ))}

        {lstBlogs?.length === 0 && (
          <h1 className="no__blogs">Invalid search!</h1>
        )}
      </div>
    </div>
  );
};

export default Blogs;
