import React, { useEffect, useState } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { selectUserInput, setBlogData } from "../features/userSlice";

import "../styling/blogs.css";

const handleViewCommnets = (event) => {
  console.log("handleViewCommnets method called");
  console.log("event ::",event);
  console.log("event.target.id :",event.target.id);
  return (
  <div class="modal-dialog modal-dialog-centered modal-dialog-scrollable">
      <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div class="modal-dialog">
        <div class="modal-content">
          <div class="modal-header">
            <h1 class="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div class="modal-body">
            ...
          </div>
          <div class="modal-footer">
            <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
            <button type="button" class="btn btn-primary">Save changes</button>
          </div>
        </div>
      </div>
    </div>
  </div>)
} 
const Blogs = () => {
  const searchInput = useSelector(selectUserInput);
  console.log("Going to fetch API");
  // const blog_url =fetch('https://jsonplaceholder.typicode.com/todos/1')
  // .then(response => response.json())
  // .then(json => {
  // })
  console.log("After API call");
  const dispatch = useDispatch();
  const [lstBlogs, setBlogs] = useState();

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((response) => {
        console.log("Data:", response.data);
        dispatch(setBlogData(response.data));
        setBlogs(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [searchInput]);
  console.log("Test");

  return (
    <div className="blog__page">
      <h1 className="blog__page__header">Blogs</h1>
      {loading ? <h1 className="loading">Loading...</h1> : ""}
      <div className="blogs">
        {lstBlogs?.map((blogObj) => (
          
          <a
            className="blog"
            target="_blank"
            href={blogObj?.url}
            rel="noopener noreferrer"
            key={blogObj.id}
          >
            <div>
              <h3 className="sourceName">
                <span>{blogObj?.title}</span>
                <p>{blogObj?.publishedAt}</p>
              </h3>
              <p>{blogObj?.body}</p>
              <div className="commentCls" id={blogObj?.id} onClick={handleViewCommnets}>ðŸ’¬</div>
            </div>
          </a>
        ))}

        {lstBlogs?.length === 0 && (
          <h1 className="no__blogs">Invalid search!</h1>
        )}
      </div>
    </div>
  );
};

export default Blogs;
