import React, { useEffect, useState } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { selectUserInput, setBlogData } from "../features/userSlice";

import "../styling/blogs.css";

const Blogs = () => {
  const searchInput = useSelector(selectUserInput);
  console.log("Going to fetch API");
  const blog_url =fetch('https://jsonplaceholder.typicode.com/todos/1')
  .then(response => response.json())
  .then(json => {
  })
  console.log("After API call");
  const dispatch = useDispatch();
  const [blogs, setBlogs] = useState();

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get('https://jsonplaceholder.typicode.com/todos/1')
      .then((response) => {
        console.log("Data:", response.data)
        dispatch(setBlogData(response.data));
        setBlogs(response.data);
        setLoading(false);
      }) 
      .catch((error) => {
        console.log(error);
      });
  }, [searchInput]);
  console.log("Test")

  return (
    <div className="blog__page">
      <h1 className="blog__page__header">Blogs</h1>
      {loading ? <h1 className="loading">Loading...</h1> : ""}
      <div className="blogs">
      
       
          <a className="blog" target="_blank" href={blogs.url} rel="noopener noreferrer">
            <img src={blogs.image}  alt="" />
            <div>
              <h3 className="sourceName">
                <span>{blogs.source.name}</span>
                <p>{blogs.publishedAt}</p>
              </h3>
              <h1>{blogs.title}</h1>
              <p>{blogs.description}</p>
            </div>
          </a>
        

        {blogs?.totalArticles === 0 && (
          <h1 className="no__blogs">
            Invalid search!
          </h1>
        )}
      </div>
    </div>
  );
};

export default Blogs;
